/* ─────────────────────────────────────────────────────────────────
   AUTH  — in-memory account store (no backend required for beta)
   In production: replace with Firebase Auth / Supabase / Clerk
───────────────────────────────────────────────────────────────── */
const ACCOUNTS_KEY = "cognura_accounts_v1";
const SESSION_KEY  = "cognura_session_v1";

function loadAccounts() {
  try { return JSON.parse(localStorage.getItem(ACCOUNTS_KEY)||"{}"); } catch { return {}; }
}
function saveAccounts(a) {
  try { localStorage.setItem(ACCOUNTS_KEY, JSON.stringify(a)); } catch {}
}
function loadSession() {
  try { return JSON.parse(localStorage.getItem(SESSION_KEY)||"null"); } catch { return null; }
}
function saveSession(s) {
  try { localStorage.setItem(SESSION_KEY, JSON.stringify(s)); } catch {}
}
function hashPwd(p) {
  // Simple deterministic hash for demo — NOT cryptographically secure
  let h=0; for(let i=0;i<p.length;i++){h=((h<<5)-h)+p.charCodeAt(i);h|=0;} return h.toString(36);
}


