/* ─────────────────────────────────────────────────────────────────
   CYCLE HELPERS
───────────────────────────────────────────────────────────────── */
const CYCLE_PHASES = {
  menstrual:  { days:[1,5],   label:"Menstrual",   emoji:"🌑", hue:"danger",  desc:"Rest & restore. Iron-rich foods, gentle movement, extra sleep." },
  follicular: { days:[6,13],  label:"Follicular",  emoji:"🌒", hue:"warn",    desc:"Energy rising. Great for new projects, socialising, cardio." },
  ovulation:  { days:[14,16], label:"Ovulation",   emoji:"🌕", hue:"success", desc:"Peak energy & confidence. Ideal for high-intensity work & exercise." },
  luteal:     { days:[17,28], label:"Luteal",      emoji:"🌖", hue:"accent",  desc:"Wind down. Prioritise sleep, reduce caffeine, self-compassion." },
};

function getCyclePhase(cd) {
  if (!cd) return null;
  if (cd<=5)  return "menstrual";
  if (cd<=13) return "follicular";
  if (cd<=16) return "ovulation";
  if (cd<=28) return "luteal";
  return null;
}
function getMenstrualInsights(cycleDay, metrics, t) {
  const phase = getCyclePhase(cycleDay);
  if (!phase) return [];
  const insights = [];
  if (phase==="menstrual") {
    insights.push({ icon:"🌑", text:`Day ${cycleDay} – Menstrual phase. Lower energy is normal. Prioritise rest, warmth and iron-rich foods like leafy greens.` });
    if (metrics.stress>6) insights.push({ icon:"🧘", text:"High stress during menstruation worsens cramps via cortisol. Gentle yoga or heat therapy can ease symptoms significantly." });
  } else if (phase==="follicular") {
    insights.push({ icon:"🌒", text:`Day ${cycleDay} – Follicular phase. Oestrogen rising — you'll feel progressively sharper and more energised. Lean into it.` });
  } else if (phase==="ovulation") {
    insights.push({ icon:"🌕", text:`Day ${cycleDay} – Ovulation. Peak oestrogen and testosterone means peak performance. Best time for demanding work or exercise.` });
  } else if (phase==="luteal") {
    insights.push({ icon:"🌖", text:`Day ${cycleDay} – Luteal phase. Energy may dip toward days 24–28. Prioritise sleep consistency and reduce stimulants.` });
    if (cycleDay>=24) insights.push({ icon:"💜", text:"Late luteal — PMS symptoms may emerge. Magnesium glycinate (200–400 mg before bed) helps with mood, sleep and bloating." });
  }
  return insights;
}

/* ─────────────────────────────────────────────────────────────────
   HEALTH CALCS
───────────────────────────────────────────────────────────────── */
function calcBMI(w, h) { if (!w||!h) return null; return (w/((h/100)**2)).toFixed(1); }
function bmiCat(bmi) {
  if (!bmi) return null;
  const v = parseFloat(bmi);
  if (v<18.5) return { label:"Underweight", hue:"info"    };
  if (v<25)   return { label:"Healthy",     hue:"success" };
  if (v<30)   return { label:"Overweight",  hue:"warn"    };
  return        { label:"Obese",          hue:"danger"  };
}
function burnoutRisk(days) {
  if (days.length<3) return { score:0, level:"low", factors:[] };
  const r=days.slice(-3); let score=0; const f=[];
  const aS=r.reduce((s,d)=>s+d.stress,0)/r.length;
  const aSl=r.reduce((s,d)=>s+d.sleep,0)/r.length;
  const aE=r.reduce((s,d)=>s+d.energy,0)/r.length;
  const aM=r.reduce((s,d)=>s+d.mood,0)/r.length;
  const tEx=r.reduce((s,d)=>s+d.exercise,0);
  if (aS>7){score+=30;f.push("Sustained high stress");}else if(aS>5)score+=15;
  if (aSl<6.5){score+=25;f.push("Chronic sleep deficit");}else if(aSl<7)score+=10;
  if (aE<5){score+=20;f.push("Low energy levels");}
  if (aM<5){score+=15;f.push("Declining mood");}
  if (tEx<60){score+=10;f.push("Insufficient exercise");}
  return { score:Math.min(score,100), level:score>=60?"high":score>=35?"moderate":"low", factors:f };
}
function cogScore(day) {
  return Math.round((day.mood*10+day.energy*10+(10-day.stress)*10+Math.min(day.sleep/9,1)*100)/4);
}
const allTimeCog = days => Math.round(days.reduce((s,d)=>s+cogScore(d),0)/days.length);

