function generateNotifs(days, profile) {
  if (!days.length) return [];
  const today=days[days.length-1];
  const notes=[];
  const a3s=days.slice(-3).reduce((s,d)=>s+d.stress,0)/Math.min(3,days.length);
  const a3sl=days.slice(-3).reduce((s,d)=>s+d.sleep,0)/Math.min(3,days.length);
  if (today.sleep<6.5) notes.push({id:"sleep-low",  type:"warn",    icon:"🌙", title:"Sleep Debt",           body:`Only ${today.sleep}h logged — ${(7-today.sleep).toFixed(1)}h below minimum.`,              action:"Dim lights 1h before bed, same wake time daily."});
  if (a3sl<6.8&&days.length>=3) notes.push({id:"sleep-t",type:"danger",icon:"📉",title:"Chronic Sleep Trend",  body:`3-day average: ${a3sl.toFixed(1)}h. Sleep debt accumulates quickly.`,                    action:"Move bedtime 30 min earlier each night this week."});
  if (today.stress>7) notes.push({id:"stress",      type:"danger",  icon:"⚡", title:"High Stress",           body:`Stress at ${today.stress}/10. Elevated cortisol impairs memory and focus.`,               action:"Try 4-7-8 breathing: 4s inhale, 7s hold, 8s exhale × 4."});
  if (a3s>6.5&&days.length>=3) notes.push({id:"burnout",type:"danger", icon:"🔥",title:"Burnout Risk Rising",  body:`Stress elevated 3+ days (avg ${a3s.toFixed(1)}/10).`,                                    action:"Schedule 2h of screen-free rest today."});
  if (today.exercise===0) notes.push({id:"no-ex",    type:"info",    icon:"◎",  title:"No Movement Today",    body:"Even 10 minutes of walking releases BDNF and reduces cortisol.",                           action:"15-min walk — even after dark counts."});
  if (today.screenTime>8) notes.push({id:"screen-high",    type:"warn",    icon:"📱",  title:"High Screen Time",      body:`${today.screenTime}h on screens. Blue light affects sleep quality.`,                       action:"Set phone to Night Shift after 8pm. No screens 1h before bed."});
  if (today.steps<5000) notes.push({id:"steps",      type:"info",    icon:"▲",  title:"Low Steps",            body:`${today.steps.toLocaleString()} steps. Sedentary behaviour worsens sleep and mood.`,     action:"2 000 more steps ≈ 20-min walk. Two 10-min breaks work too."});
  if (today.mood>=8&&today.energy>=7&&today.stress<=4) notes.push({id:"peak",type:"success",icon:"✦",title:"Peak Cognition Day",body:"Mood, energy and stress all optimal. You're primed for deep work.",action:"Block 2–3 h for your highest-priority task."});
  if (profile?.sex==="female"&&today.cycleDay) {
    const ph=getCyclePhase(today.cycleDay);
    if (ph==="menstrual") notes.push({id:"cyc-m",type:"info",icon:"🌑",title:"Menstrual Phase",body:`Day ${today.cycleDay} — prioritise rest and iron-rich foods.`,action:"Leafy greens + Vitamin C for absorption. Gentle movement only."});
    if (ph==="ovulation") notes.push({id:"cyc-o",type:"success",icon:"🌕",title:"Ovulation Window",body:`Day ${today.cycleDay} — peak physical and cognitive performance.`,action:"Schedule your hardest workout or most demanding work now."});
    if (ph==="luteal"&&today.cycleDay>=24) notes.push({id:"cyc-l",type:"warn",icon:"🌖",title:"Late Luteal Phase",body:`Day ${today.cycleDay} — PMS symptoms may emerge.`,action:"Magnesium glycinate 200–400 mg before bed. Reduce caffeine."});
  }
  return notes.sort((a,b)=>{ const o={danger:0,warn:1,success:2,info:3}; return (o[a.type]||3)-(o[b.type]||3); });
}

