function mHue(v, m, t) {
  const c = m.inv
    ? (v<=m.ideal[1]?t.success:v<=6?t.warn:t.danger)
    : (v>=m.ideal[0]?t.success:v>=m.ideal[0]*.75?t.warn:t.danger);
  return c;
}

