    <input type={type} value={value} onChange={onChange} placeholder={placeholder}
      style={{width:"100%",background:t.cardAlt,border:`1px solid ${t.border}`,borderRadius:12,padding:"12px 14px",color:t.textPrimary,fontSize:14,fontFamily:"inherit",outline:"none",...style}}/>
