/* ─────────────────────────────────────────────────────────────────
   ONBOARDING
───────────────────────────────────────────────────────────────── */
function Onboarding({ onComplete, isDark }) {
  const [step, setStep] = useState(0);
  const [profile, setProfile] = useState({ name:"", sex:"", age:"", weight:"", height:"", cycleLength:28, lastPeriod:"" });
  const t = THEMES[isDark?"dark":"light"];
  const up = (k,v) => setProfile(p=>({...p,[k]:v}));
  const bmi = calcBMI(profile.weight, profile.height);
  const cat = bmiCat(bmi);

  const totalSteps = profile.sex==="female" ? 4 : 3;

  const sexOptions = [
    {value:"male",   label:"Male",   emoji:"♂"},
    {value:"female", label:"Female", emoji:"♀"},
    {value:"x",      label:"X",      emoji:"✦"},
  ];

  const renderStep = () => {
    if (step===0) return (
      <div style={{textAlign:"center",paddingTop:40}}>
        <div style={{width:80,height:80,borderRadius:24,background:t.accentMid,display:"flex",alignItems:"center",justifyContent:"center",fontSize:36,margin:"0 auto 24px"}}>🧠</div>
        <h1 style={{fontFamily:"'Lora',serif",fontSize:30,fontWeight:700,color:t.textPrimary,marginBottom:12,lineHeight:1.2}}>Welcome to Cognura</h1>
        <p style={{color:t.textSecondary,fontSize:15,lineHeight:1.7,marginBottom:40}}>Your personalised wellness intelligence app. Let's set up your profile so every insight is tailored to you.</p>
        <button onClick={()=>setStep(1)} style={primaryBtn(t)}>Get Started</button>
        <p style={{color:t.textMuted,fontSize:12,marginTop:16}}>Takes about 2 minutes · Everything is optional</p>
      </div>
    );

    if (step===1) return (
      <div>
        <StepHead step={1} total={totalSteps} title="About You" sub="Personalises your health benchmarks." t={t}/>
        <FieldRow label="What should we call you?" t={t}>
          <TextInput value={profile.name} onChange={e=>up("name",e.target.value)} placeholder="Your name (optional)" t={t}/>
        </FieldRow>
        <FieldRow label="Biological sex" t={t}>
          <p style={{color:t.textMuted,fontSize:12,marginBottom:10}}>Used for health benchmarks and hormonal insights.</p>
          <ToggleChip options={sexOptions} value={profile.sex} onChange={v=>up("sex",v)} t={t}/>
        </FieldRow>
        <NavRow onBack={()=>setStep(0)} onNext={()=>setStep(2)} nextDisabled={!profile.sex} t={t}/>
      </div>
    );

    if (step===2) return (
      <div>
        <StepHead step={2} total={totalSteps} title="Body Metrics" sub="Helps calculate BMI and personalise recommendations. All optional." t={t}/>
        <FieldRow label="Age" t={t}>
          <SkipRow value={profile.age} onChange={e=>up("age",e.target.value)} placeholder="e.g. 28" onSkip={()=>up("age","")} t={t}/>
        </FieldRow>
        <FieldRow label="Height (cm)" t={t}>
          <SkipRow value={profile.height} onChange={e=>up("height",e.target.value)} placeholder="e.g. 168" onSkip={()=>up("height","")} t={t}/>
        </FieldRow>
        <FieldRow label="Weight (kg)" t={t}>
          <SkipRow value={profile.weight} onChange={e=>up("weight",e.target.value)} placeholder="e.g. 65" onSkip={()=>up("weight","")} t={t}/>
        </FieldRow>
        {bmi&&cat&&(
          <div style={{background:t.cardAlt,border:`1px solid ${t.border}`,borderRadius:14,padding:"12px 16px",marginBottom:16,display:"flex",justifyContent:"space-between",alignItems:"center"}}>
            <span style={{color:t.textSecondary,fontSize:14}}>BMI</span>
            <span style={{fontWeight:700,fontSize:15,color:t[cat.hue]}}>{bmi} <span style={{fontWeight:400,fontSize:12}}>· {cat.label}</span></span>
          </div>
        )}
        <NavRow onBack={()=>setStep(1)} onNext={()=>setStep(profile.sex==="female"?3:totalSteps)} t={t}/>
      </div>
    );

    if (step===3&&profile.sex==="female") return (
      <div>
        <StepHead step={3} total={4} title="Cycle Tracking" sub="Hormone-aware insights for every phase of your cycle." t={t}/>
        <div style={{background:t.roseSoft,border:`1px solid ${t.rose}30`,borderRadius:14,padding:"12px 14px",marginBottom:20}}>
          <p style={{fontSize:13,color:t.textSecondary,lineHeight:1.7,margin:0}}>🌸 Tracking your cycle helps Cognura anticipate energy patterns, mood shifts and recovery needs tied to your hormonal phases.</p>
        </div>
        <FieldRow label="Average cycle length (days)" t={t}>
          <div style={{display:"flex",gap:8,alignItems:"center"}}>
            <input type="number" value={profile.cycleLength} onChange={e=>up("cycleLength",parseInt(e.target.value)||28)} min={21} max={40}
              style={{flex:1,background:t.cardAlt,border:`1px solid ${t.border}`,borderRadius:12,padding:"12px 14px",color:t.textPrimary,fontSize:14,fontFamily:"inherit",outline:"none"}}/>
            <span style={{color:t.textMuted,fontSize:13}}>days</span>
          </div>
        </FieldRow>
        <FieldRow label="First day of last period" t={t}>
          <SkipRow value={profile.lastPeriod} onChange={e=>up("lastPeriod",e.target.value)} placeholder="Select date" type="date" onSkip={()=>up("lastPeriod","")} t={t}/>
        </FieldRow>
        {profile.lastPeriod&&(()=>{
          const diff=Math.floor((new Date()-new Date(profile.lastPeriod))/(1000*60*60*24))+1;
          const cd=((diff-1)%(profile.cycleLength||28))+1;
          const ph=getCyclePhase(cd); const pd=ph?CYCLE_PHASES[ph]:null;
          return pd?(
            <div style={{background:t.accentSoft,border:`1px solid ${t.accent}30`,borderRadius:14,padding:"12px 16px",marginBottom:16}}>
              <p style={{fontSize:12,fontWeight:600,color:t.accent,marginBottom:2}}>{pd.emoji} Day {cd} — {pd.label}</p>
              <p style={{fontSize:12,color:t.textMuted,lineHeight:1.5}}>{pd.desc}</p>
            </div>
          ):null;
        })()}
        <NavRow onBack={()=>setStep(2)} onNext={()=>setStep(4)} t={t}/>
      </div>
    );

    // Final step
    const finalStep = profile.sex==="female" ? 4 : 3;
    if (step===finalStep) return (
      <div style={{textAlign:"center"}}>
        <div style={{width:64,height:64,borderRadius:20,background:t.accentMid,display:"flex",alignItems:"center",justifyContent:"center",fontSize:28,margin:"0 auto 20px"}}>✓</div>
        <h2 style={{fontFamily:"'Lora',serif",fontSize:26,fontWeight:700,color:t.textPrimary,marginBottom:8}}>{profile.name?`You're ready, ${profile.name}!`:"All set!"}</h2>
        <p style={{color:t.textSecondary,fontSize:14,lineHeight:1.7,marginBottom:24}}>Cognura will personalise every insight, notification and AI suggestion based on your profile.</p>
        <div style={{background:t.cardAlt,border:`1px solid ${t.border}`,borderRadius:16,padding:16,marginBottom:28,textAlign:"left"}}>
          {[["Sex",profile.sex||"—"],["Age",profile.age?profile.age+" yrs":"Skipped"],["Height",profile.height?profile.height+"cm":"Skipped"],["Weight",profile.weight?profile.weight+"kg":"Skipped"],["BMI",bmi||(profile.weight&&profile.height?"—":"Skipped")],...(profile.sex==="female"?[["Cycle",profile.cycleLength+" days"]]:[])]
            .map(([k,v])=>(
              <div key={k} style={{display:"flex",justifyContent:"space-between",padding:"8px 0",borderBottom:`1px solid ${t.border}`}}>
                <span style={{color:t.textMuted,fontSize:13}}>{k}</span>
                <span style={{color:t.textPrimary,fontSize:13,fontWeight:600}}>{v}</span>
              </div>
            ))}
        </div>
        <button onClick={()=>onComplete(profile)} style={primaryBtn(t)}>Enter Cognura →</button>
      </div>
    );
  };

  function StepHead({step,total,title,sub,t}) {
    return (
      <div style={{marginBottom:28}}>
        <div style={{display:"flex",alignItems:"center",gap:10,marginBottom:16}}>
          <span style={{fontSize:12,fontWeight:600,color:t.textMuted}}>{step} / {total}</span>
          <div style={{flex:1,height:3,background:t.border,borderRadius:3}}>
            <div style={{height:3,background:t.accent,borderRadius:3,width:`${(step/total)*100}%`,transition:"width 0.4s ease"}}/>
          </div>
        </div>
        <h2 style={{fontFamily:"'Lora',serif",fontSize:24,fontWeight:700,color:t.textPrimary,marginBottom:4}}>{title}</h2>
        <p style={{color:t.textMuted,fontSize:13}}>{sub}</p>
      </div>
    );
  }
  function NavRow({onBack,onNext,nextDisabled,t}) {
    return (
      <div style={{display:"flex",gap:10,marginTop:8}}>
        <button onClick={onBack} style={{...ghostBtn(t),flex:.45}}>← Back</button>
        <button onClick={onNext} disabled={nextDisabled} style={{...primaryBtn(t),flex:1,opacity:nextDisabled?.4:1}}>Continue →</button>
      </div>
    );
  }

  return (
    <div style={{minHeight:"100vh",background:t.bg,color:t.textPrimary,fontFamily:"'Plus Jakarta Sans',sans-serif",maxWidth:430,margin:"0 auto",padding:"48px 20px 60px",overflowX:"hidden"}}>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700&family=Lora:wght@500;600;700&display=swap');
        *{box-sizing:border-box;margin:0;padding:0}
        input{outline:none;font-family:inherit}
        input[type=range]{-webkit-appearance:none;height:4px;border-radius:4px;background:${t.border}}
        input[type=range]::-webkit-slider-thumb{-webkit-appearance:none;width:18px;height:18px;border-radius:50%;background:${t.accent};cursor:pointer;box-shadow:0 1px 4px rgba(0,0,0,0.2)}
        input[type=date]::-webkit-calendar-picker-indicator{filter:${isDark?"invert(.5)":"none"}}
        button{font-family:inherit}
        @keyframes fadeIn{from{opacity:0;transform:translateY(8px)}to{opacity:1;transform:translateY(0)}}
        .fade{animation:fadeIn 0.3s ease forwards}
      `}</style>
      <div key={step} className="fade">{renderStep()}</div>
    </div>
  );
}

const primaryBtn = (t) => ({width:"100%",padding:"14px",background:t.accent,color:"#ffffff",border:"none",borderRadius:14,fontSize:15,fontWeight:600,cursor:"pointer",fontFamily:"inherit",transition:"opacity 0.15s"});
const ghostBtn   = (t) => ({padding:"14px",background:"transparent",color:t.textSecondary,border:`1px solid ${t.border}`,borderRadius:14,fontSize:14,cursor:"pointer",fontFamily:"inherit",textAlign:"center"});

